import "./profile.css"
import {Footer} from "../../components";
import {useAuth} from "../../hooks";
import {useParams} from "react-router-dom";
import {useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {fetchUser, getProfile} from "../../redux/usersSlice";
import ProfilePosts from "../../components/ProfilePosts/ProfilePosts";
import PowerSettingsNewOutlinedIcon from '@mui/icons-material/PowerSettingsNewOutlined';

const Profile = () => {

    const auth = useAuth()
    const {username} = useParams()
    const dispatch = useDispatch()
    const user = useSelector(getProfile)

    useEffect(() => {
        if (auth.username !== username) {
            dispatch(fetchUser({username}))
        }
    }, [username]);

    let profile
    let options
    if (auth.username === username) {
        profile = auth
        options = <>
            <button className="profile-btn">Edit profile</button>
            <button className="profile-logout"><PowerSettingsNewOutlinedIcon/></button>
        </>
    } else {
        profile = user
        options = <>
            <button className="profile-follow">Follow</button>
            <button className="profile-btn">Message</button>
        </>
    }

    return (
        <main className="profile-container">
            <div className="main-profile">
                <div className="main-profile-left">
                    <img src={profile?.avatar? profile?.avatar : "/img/avatar.jpg"} alt=""/>
                </div>
                <div className="main-profile-right">
                    <div className="right-top">
                        <span>{username}</span>
                        {options}
                    </div>
                    <div className="right-mid">
                        <ul>
                            <li><span className="profile-stat-count">164</span> posts</li>
                            <li><span className="profile-stat-count">{profile?.followers?.length}</span> followers</li>
                            <li><span className="profile-stat-count">{profile?.followings?.length}</span> following</li>
                        </ul>
                    </div>
                    <div className="right-bot">
                        <p className="profile-name">Real Madrid C.F.</p>
                        <p>
                            ⚽️ Official profile of Real Madrid C.F.<br/>
                            🏆 14 times European Champions<br/>
                            🌍 FIFA Best Club of the 20th Century<br/>
                            📱 #RealFootball | 🤍 #MADRIDISTAS
                        </p>
                    </div>
                </div>
            </div>
            <ProfilePosts userId={profile?._id}/>
            <Footer/>
        </main>
    )
}

export default Profile
