import "./profile.css"
import {Footer} from "../../components";
import {useAuth} from "../../hooks";
import {Link, useParams} from "react-router-dom";
import {useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {clearUsers, fetchUser, getProfile} from "../../redux/usersSlice";
import ProfilePosts from "../../components/ProfilePosts/ProfilePosts";
import PowerSettingsNewOutlinedIcon from '@mui/icons-material/PowerSettingsNewOutlined';
import {signOut} from "../../redux/authSlice";
import {clearPosts, getPosts} from "../../redux/postsSlice";
import {followUser} from "../../redux/usersSlice";

const Profile = () => {

    const auth = useAuth()
    const {username} = useParams()
    const dispatch = useDispatch()
    const user = useSelector(getProfile)
    const posts = useSelector(getPosts)

    useEffect(() => {
        if (auth.username !== username) {
            dispatch(fetchUser({username}))
        }
        /*return () => {
            dispatch(clearUsers())
        }*/
    }, [username]);

    let profile
    let options
    if (auth.username === username) {
        profile = auth
        options = <>
            <Link to={`/accounts`}>
                <button className="profile-btn">
                    Edit profile
                </button>
            </Link>

            <button className="profile-logout"
                    onClick={() => {
                        dispatch(signOut())
                        dispatch(clearUsers())
                        dispatch(clearPosts())
                    }
                }
            >
                <PowerSettingsNewOutlinedIcon/>
            </button>
        </>
    } else {
        profile = user
        const following = user?.followers?.find((follow) => follow === auth._id)
        options = <>
            <button className={following ? "profile-following" : "profile-follow"}
                    onClick={() => dispatch(followUser({id: auth._id, userId: user._id}))}>
                {following ? "Following" : "Follow" }
            </button>
            <button className="profile-btn">Message</button>
        </>
    }

    return (
        <main className="profile-container">
            <div className="main-profile">
                <div className="main-profile-left">
                    <img src={profile?.avatar? profile?.avatar : "/img/avatar.jpg"} alt=""/>
                </div>
                <div className="main-profile-right">
                    <div className="right-top">
                        <span>{username}</span>
                        {options}
                    </div>
                    <div className="right-mid">
                        <ul>
                            <li><span className="profile-stat-count">{posts?.length}</span> posts</li>
                            <li><span className="profile-stat-count">{profile?.followers?.length}</span> followers</li>
                            <li><span className="profile-stat-count">{profile?.followings?.length}</span> following</li>
                        </ul>
                    </div>
                    <div className="right-bot">
                        <p className="profile-name">{profile?.name}</p>
                        <p>
                            {profile?.info}
                        </p>
                    </div>
                </div>
            </div>
            <ProfilePosts userId={profile?._id}/>
            <Footer/>
        </main>
    )
}

export default Profile
