import "./search.css"
import UsersList from "../../components/UsersList/UsersList";
import {useEffect, useRef, useState} from "react";
import {Link} from "react-router-dom";
import {useDispatch} from "react-redux";
import {fetchSearch} from "../../redux/searchSlice";
import useDebounce from "../../hooks/useDebounce";

const Search = ({collapse, setCollapse, setSelectedMenu, location}) => {

    const dispatch = useDispatch()
    const refSearch = useRef()

    const [search, setSearch] =useState('')
    const debouncedSearchTerm = useDebounce(search, 500);

    useEffect(() => {
        if (search) {
            const promise = dispatch(fetchSearch({
                search: encodeURIComponent(search)
            }))
            return () => {
                // `createAsyncThunk` attaches an `abort()` method to the promise
                promise.abort()
            }
        }
    },[search])

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (refSearch.current && !refSearch.current.contains(event.target)) {
                setCollapse(false);
                setSelectedMenu(location.pathname)
                setSearch('')
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [refSearch]);

    return (
        <section>
            {collapse && (
                <div className="search-bar" ref={refSearch}>
                    <div>
                        <h1>Search</h1>
                        <input
                            value={search}
                            placeholder="Search"
                            onChange={(e) => setSearch(e.target.value)}
                        />
                    </div>
                    <div className="search-preview">
                        <Link to={'/'}>
                            <UsersList/>
                        </Link>

                        <UsersList/>
                        <UsersList/>
                        <UsersList/>
                        <UsersList/>
                        <UsersList/>
                    </div>
                </div>
            )}
        </section>
    )
}

export default Search
