import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import axios from "../axios/axios";

const initialState = {
    auth: [],
    success: false,
    error : null
}

export const signUp = createAsyncThunk('auth/signUp',async ({user, pwd}, {rejectWithValue}) => {
    try {
        const response = await axios.post('/register', {user, pwd})
        return response.data
    } catch (err) {
        if (!err?.response) {
            return rejectWithValue('No Server Response');
        } else if (err.response?.status === 409) {
            return rejectWithValue('Username Taken');
        } else {
            return rejectWithValue('Registration Failed')
        }
    }
})

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        reset: (state) => {
            state.success = false
            state.error = null
        }
    },
    extraReducers(builder){
        builder
            .addCase(signUp.fulfilled,(state,action) => {
                state.success = true
                state.error = null
            })
            .addCase(signUp.rejected, (state, action) => {
                state.error = action.payload
            })
    }
})

export const getError = (state) => state.auth.error
export const getSuccess = (state) => state.auth.success
export default authSlice.reducer
