import "./PostCard.css"
import {ChatBubbleOutline, Favorite,
    FavoriteBorder, Share,
    BookmarkBorder, MoreHoriz,
    SentimentSatisfiedAlt, Bookmark} from "@mui/icons-material";
import TimeAgo from "../../pages/PostDetail/TimeAgo";
import {useDispatch} from "react-redux";
import {useAuth} from "../../hooks";
import {addCmt, likePost} from "../../redux/postsSlice";
import {useState} from "react";
import {Link} from "react-router-dom";
import savedPost from "../../pages/savedPost/SavedPost";
import {savePost} from "../../redux/usersSlice";
import Like from "../Like";
import Saved from "../Saved";

const PostCard = ({post}) => {

    const auth = useAuth()
    const dispatch = useDispatch()

    const [cmt, setCmt] = useState('')
    const [likes, setLikes] = useState(post.likes)

    const handleCmt = () => {
        dispatch(addCmt({id: post._id, author: auth._id, cmt: cmt}))
    }

    return (
        <div className="post">
            <div className="info">
                <div className="user css-link">
                    <div className="profile-pic ">
                        <Link to={`/${post?.username}`}>
                            <img src={post?.avatar ? post.avatar : "/img/avatar.jpg"} alt=""/>
                        </Link>
                    </div>
                    <p className="username ">
                        <Link to={`/${post?.username}`}>
                            {post ? post.username : 'Unknown author'}
                        </Link>
                    </p>
                    <p className="post-time">
                        &nbsp;â€¢&nbsp;<TimeAgo timestamp={post.createdAt}/>
                    </p>
                </div>
                {post.userId === auth._id
                    && <button><MoreHoriz/></button>
                }
            </div>
            <div className="post-image">
                <img src={post.img} alt="" />
            </div>
            <div className="post-content css-link">
                <div className="reaction-wrapper">
                    <div>
                        <Like
                            post={post}
                            auth={auth}
                            likes={likes}
                            setLikes={setLikes}
                        />
                        <Link to={`p/${post._id}`}>
                            <button style={{margin: "0 15px", color: "black"}}>
                                    <ChatBubbleOutline fontSize="large"/>
                            </button>
                        </Link>
                        <button>
                            <Share fontSize="large"/>
                        </button>
                    </div>
                    <Saved post={post} auth={auth}/>
                </div>
                <p className="likes">
                    {likes.length} {likes.length > 2 ? "likes" : "like"}
                </p>
                <p className="description">
                    <span>
                        <Link to={`/${post?.username}`}>
                            {post ? post.username : 'Unknown author'}
                        </Link>
                    </span>
                    {post.desc}
                </p>
                <Link to={`p/${post._id}`}>
                    <p className="post-cmt">View all {post.comments.length} comments</p>
                </Link>
            </div>
            <div className="comment-wrapper">
                <SentimentSatisfiedAlt/>
                <input
                    type="text"
                    className="comment-box"
                    placeholder="Add a comment"
                    value={cmt}
                    onChange={(e) => setCmt(e.target.value)}
                />
                <button
                    className="comment-btn"
                    onClick={handleCmt}
                >
                    Post
                </button>
            </div>
        </div>
    )
}

export default PostCard
