import "./explore.css"
import PostSquare from "../../components/PostSquare/PostSquare";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {fetchExplorePosts, getPosts, getPostsError, getPostsStatus} from "../../redux/postsSlice";
import {useAuth} from "../../hooks";
import {Footer} from "../../components";

const Explore = () => {

    const dispatch = useDispatch()
    const auth = useAuth()
    const posts = useSelector(getPosts)
    const postStatus = useSelector(getPostsStatus);
    const error = useSelector(getPostsError);

    useEffect(() => {
        dispatch(fetchExplorePosts({userId: auth._id}))
    }, []);

    let content;
    if (postStatus === 'loading') {
        content = <p>"Loading..."</p>;
    } else if (postStatus === 'succeeded') {
        content = posts.map(post => <PostSquare key={post.id} post={post} />)
    } else if (postStatus === 'failed') {
        content = <p>{error}</p>;
    }

    return (
        <div className="explore-container">
            <div className="gallery">
                {content}
            </div>
            <Footer/>
        </div>
    )
}

export default Explore
