import "./create.css"
import CloseIcon from '@mui/icons-material/Close';
import {useState} from "react";
import FileBase from 'react-file-base64';

const Create = ({toggleCreate, setToggleCreate, prevMenu, setSelectedMenu, avatar, username}) => {

    const [picture, setPicture] = useState(null);
    const [imgData, setImgData] = useState(null);

    /*const onChangePicture = e => {
        if (e.target.files[0]) {
            console.log("picture: ", e.target.files);
            setPicture(e.target.files[0]);
            const reader = new FileReader();
            reader.addEventListener("load", () => {
                setImgData(reader.result);
            });
            reader.readAsDataURL(e.target.files[0]);
        }
    };*/

    return (
        <form>
            {toggleCreate && (
                <div className="overlay">
                    <button
                        className="closebtn"
                        onClick={() => {
                            setToggleCreate(false)
                            setSelectedMenu(prevMenu)
                            setPicture(null)
                            setImgData(null)
                        }}
                    >
                        <CloseIcon fontSize="large" sx={{ color: 'white' }}/>
                    </button>

                    <div className="overlay-content">
                        <div className="overlay-content-header">
                            Create new post
                            <button className="share-btn">Share</button>
                        </div>
                            <div className="overlay-content-main">

                                <div className="overlay-content-left">
                                    <div className="overlay-user">
                                        <div className="overlay-profile-pic">
                                            <img src={avatar? avatar : "/img/avatar.jpg"} alt=""/>
                                        </div>
                                        <p className="overlay-username">{username}</p>
                                    </div>
                                    <textarea placeholder="Write a caption..."/>
                                </div>

                                <div className="overlay-content-right">
                                    <label htmlFor="file-upload" className="custom-file-upload">
                                        Select image from computer
                                    </label>
                                    <FileBase
                                        id="file-upload"
                                        type="file"
                                        accept=".png,.jpeg,.jpg"
                                        onDone={({ base64 }) => setPicture(base64)}
                                    />
                                    <input
                                        id="file-upload"
                                        type="file"
                                        accept=".png,.jpeg,.jpg"
                                        onChange={onChangePicture}
                                    />
                                    <img src={picture? imgData : "/img/no-photo-available.png"} alt=""/>
                                </div>

                            </div>
                    </div>
                </div>
            )}
        </form>
    )
}

export default Create
