import TimeAgo from "./TimeAgo";
import {Close} from "@mui/icons-material";
import {deleteCmt} from "../../redux/postsSlice";

const Cmt = ({cmt, dispatch, postId}) => {

    return (
        <div className="detail-info-cmt">
            <div><img src={cmt?.avatar ? cmt.avatar : "/img/avatar.jpg"} alt=""/></div>
            <div>
                <div className="cmt-container">
                            <span className="cmt">
                                <span className="cmt-user">{cmt.username}</span>
                                {cmt.cmt}
                            </span>
                </div>
                <div className="cmt-time"><p><TimeAgo timestamp={cmt.date}/></p></div>
                <button
                    onClick={() =>
                        dispatch(deleteCmt({
                            id: postId,
                            cmtId: cmt._id,
                        }))
                    }
                >
                    <Close fontSize="small"/>
                </button>
            </div>
        </div>
    )
}

export default Cmt
