import {useDispatch} from "react-redux";
import {clearStatistics, getAllUsers} from "../../redux/adminSlice";
import {useEffect} from "react";
import {Link} from "react-router-dom";
import {followUser} from "../../redux/usersSlice";
import {newNotification} from "../../redux/notificationSlice";

const Management = () => {

    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(getAllUsers())
        return () => {
            dispatch(clearStatistics())
        };
    }, []);


    return (
        <div style={{width: '600px'}}>
            <h1>User Management</h1>
            <div>
                <div className="user_list">
                    <div className="user_list_detail">
                        <Link to={`/`}>
                            <img src="/img/avatar.jpg" alt=""/>
                        </Link>
                        <Link to={`/`}>
                            &nbsp;&nbsp;User
                        </Link>
                    </div>
                    <div>
                        <button className='following'>Edit</button>
                        <button className='following'>Ban</button>
                        <button className='following'>Delete</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Management
