import {useEffect, useRef} from "react";
import {useDispatch, useSelector} from "react-redux";
import {useAuth} from "../../hooks";
import {getNotification, getNotifications, getNtfcStatus} from "../../redux/notificationSlice";
import {Link} from "react-router-dom";
import Loading from "../Loading/Loading";
import SearchResult from "../../pages/search/SearchResult";

const Notifications = ({collapse, selectedMenu, setCollapse}) => {

    const auth = useAuth()
    const dispatch = useDispatch()
    const notifications = useSelector(getNotifications)
    const status = useSelector(getNtfcStatus)
    const refNtfc = useRef()

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (refNtfc.current && !refNtfc.current.contains(event.target)) {
                setCollapse(false)
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [refNtfc]);

    useEffect(() => {
        dispatch(getNotification({id: auth?._id}))
    },[notifications])

    let content;
    if (status === 'loading') {
        content = <Loading/>;
    } else if (status === 'succeeded') {
        content = notifications.map((s,index) =>
            <Link
                to={`/p/${s.postId}`}
                key={index}
                className="user_list search-hover"
            >
                <div className="user_list_detail">
                    <img
                        src="/img/avatar.jpg"
                        alt=""
                    />
                    &nbsp;&nbsp;
                    <div>
                        {s.username} {s.text}
                    </div>
                </div>
            </Link>
        )
    } else if (status === 'failed') {
        content = <p>ERROR</p>;
    }

    return (
        <section>
            {(collapse && selectedMenu === "notification") && (
                <div className="search-bar" ref={refNtfc}>
                    <div>
                        <h1>Notifications</h1>
                    </div>
                    <div className="search-preview">
                        {content}
                    </div>
                </div>
            )}
        </section>
    )
}

export default Notifications
