import "./PostCard.css"
import {ChatBubbleOutline, Favorite,
    FavoriteBorder, Share,
    BookmarkBorder, MoreHoriz,
    SentimentSatisfiedAlt} from "@mui/icons-material";
import PostAuthor from "../../pages/PostDetail/postAuthor";
import TimeAgo from "../../pages/PostDetail/TimeAgo";
import PostAvatar from "../../pages/PostDetail/postAvatar";
import {useDispatch} from "react-redux";
import {useAuth} from "../../hooks";
import {likePost} from "../../redux/postsSlice";
import {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import {axiosPrivate} from "../../axios/axios";

const PostCard = ({post}) => {

    const auth = useAuth()
    const dispatch = useDispatch()

    const [author, setAuthor] = useState({
        username: '',
        avatar: '',
    })
    const [likes, setLikes] = useState(post?.likes)
    const hasLikedPost = post.likes.find((like) => like === auth._id)

    const handleLike = () => {
        dispatch(likePost({id: post._id, userId: auth._id}))
            .unwrap()
            .then()
            .catch(err => {
                return err.message
            })
        if (hasLikedPost) {
            setLikes(post.likes.filter((id) => id !== auth._id));
        } else {
            setLikes([...post.likes, auth._id]);
        }
    }

    useEffect( () => {
        const fetch = async () => {
                const response = await axiosPrivate.get(`/users/p/${post.userId}`)
                const username = response?.data?.username
                const avatar = response?.data?.avatar
                console.log(username)
                setAuthor({username, avatar})
        }
        const result = fetch()
            .catch(console.error)
    })

    console.log(author)

    return (
        <div className="post">
            <div className="info">
                <div className="user css-link">
                    <div className="profile-pic ">
                        <img src={author?.avatar ? author.avatar : "/img/avatar.jpg"} alt=""/>
                    </div>
                    <p className="username ">
                        {author ? author.username : 'Unknown author'}
                    </p>
                    &nbsp;•&nbsp;<TimeAgo timestamp={post.createdAt}/>
                </div>
                <button><MoreHoriz/></button>
            </div>
            <div className="post-image">
                <img src={post.img} alt="" />
            </div>
            <div className="post-content">
                <div className="reaction-wrapper">
                    <div>
                        <button onClick={handleLike}>
                            {likes.find((like) => like === auth._id)
                                ? <Favorite fontSize="large" sx={{ color: "#f73378" }}/>
                                : <FavoriteBorder fontSize="large"/>
                            }
                        </button>
                        <Link to={`p/${post._id}`}>
                            <button style={{margin: "0 15px", color: "black"}}>
                                    <ChatBubbleOutline fontSize="large"/>
                            </button>
                        </Link>
                        <button>
                            <Share fontSize="large"/>
                        </button>
                    </div>
                    <button><BookmarkBorder fontSize="large"/></button>
                </div>
                <p className="likes">
                    {likes.length} {likes.length > 2 ? "likes" : "like"}
                </p>
                <p className="description css-link">
                    <span>{author ? author.username : 'Unknown author'}</span>
                    {post.desc}
                </p>
                <Link to={`p/${post._id}`}>
                    <p className="post-cmt">View all {post.comments.length} comments</p>
                </Link>
            </div>
            <div className="comment-wrapper">
                <SentimentSatisfiedAlt/>
                <input type="text" className="comment-box" placeholder="Add a comment"/>
                <button className="comment-btn">post</button>
            </div>
        </div>
    )
}

export default PostCard
