import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import {axiosPrivate} from "../axios/axios";

const initialState = {
    posts: [],
    post: [],
    status: 'idle', //'idle' | 'loading' | 'succeeded' | 'failed'
    error: null
}

export const addNewPost = createAsyncThunk('posts/addNewPost', async ({id, caption, imgData}) => {
    try {
        const response = await axiosPrivate.post('/posts', {id, caption, imgData})
        return response.data
    } catch (err) {
        return err.message
    }
})

export const fetchPostDetail = createAsyncThunk('posts/fetchPostDetail', async ({id}) => {
    try {
        const response = await axiosPrivate.get(`/posts/p/${id}`)
        return response.data
    } catch (err) {
        return err.message
    }
})

export const fetchHomePosts = createAsyncThunk('posts/fetchHomePosts', async ({userId}) => {
    try {
        const response = await axiosPrivate.get(`/posts/home/${userId}`)
        return response.data
    } catch (err) {
        return err.message
    }
})

export const fetchExplorePosts = createAsyncThunk('posts/fetchExplorePosts', async ({userId}) => {
    try {
        const response = await axiosPrivate.get(`/posts/explore/${userId}`)
        return response.data
    } catch (err) {
        return err.message
    }
})

export const fetchProfilePosts = createAsyncThunk('posts/fetchProfilePosts', async ({userId}) => {
    try {
        const response = await axiosPrivate.get(`/posts/${userId}`)
        return response.data
    } catch (err) {
        return err.message
    }
})

export const likePost = createAsyncThunk('posts/likePost', async ({id, userId}) => {
    try {
        const response = await axiosPrivate.patch(`/posts/${id}/like`, {userId})
        return response.data
    } catch (err) {
        return err.message
    }
})

const postsSlice = createSlice({
    name: 'posts',
    initialState,
    reducers: {
        reset(state,action) {
            state.post = null
        },
        clearPosts(state,action) {
            return {...initialState}
        }
    },
    extraReducers(builder){
        builder

            .addCase(addNewPost.fulfilled, (state, action) => {
                state.posts.push(action.payload)
            })
            .addCase(addNewPost.rejected, (state, action) => {
                state.error = action.err.message
            })

            .addCase(fetchPostDetail.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchPostDetail.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.post = action.payload
            })

            .addCase(fetchHomePosts.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchHomePosts.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.posts = action.payload
            })

            .addCase(fetchExplorePosts.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchExplorePosts.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.posts = action.payload
            })

            .addCase(fetchProfilePosts.pending, (state, action) => {
                state.status = 'loading'
            })
            .addCase(fetchProfilePosts.fulfilled, (state, action) => {
                state.status = 'succeeded'
                state.posts = action.payload
            })

            .addCase(likePost.fulfilled, (state, action) => {
                const { _id } = action.payload;
                const posts = state.posts.filter(post => post._id !== _id);
                state.posts = [...posts, action.payload];
            })

    }
})

export const getPosts = (state) => state.posts.posts
export const getPost = (state) => state.posts.post
export const getPostsStatus = (state) => state.posts.status;
export const getPostsError = (state) => state.posts.error;
export const getPostsbyID = (state, postID) => state.posts.posts.find(post => post._id === postID)
export const {reset, clearPosts} = postsSlice.actions
export default postsSlice.reducer
