import {Link, useLocation, useNavigate, useParams} from "react-router-dom";
import UsersList from "../../components/UsersList/UsersList";
import {useContext, useEffect, useRef} from "react";
import CreateContext from "../../components/context/ContextProvider";

const Modal = () => {

    const navigate = useNavigate();
    const location = useLocation()
    const refModal = useRef()
    const {setModalOpen} = useContext(CreateContext)

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (refModal.current && !refModal.current.contains(event.target)) {
                navigate(-1)
                setModalOpen(false)
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [refModal]);

    return (
        <div className="modalDiv">
            <div className="modal" ref={refModal}>

                <div className="modal-header">
                    <h4>
                        {location.pathname.includes('followers') ? 'Followers' : 'Followings'}
                    </h4>
                </div>

                <div style={{ width: '100%', padding: '10px', marginTop: '40px'}}>
                    <UsersList/>
                </div>


            </div>
        </div>
    );
};

export default Modal
