import "./postList.css"
import {Footer, PostCard} from "../index";
import {useAuth} from "../../hooks";
import {useDispatch, useSelector} from "react-redux";
import {fetchExplorePosts, getPosts, getPostsError, getPostsStatus} from "../../redux/postsSlice";
import {useEffect} from "react";
import Loading from "../Loading/Loading";

const PostList = () => {

    const auth = useAuth()
    const dispatch = useDispatch()
    const posts = useSelector(getPosts)
    const postStatus = useSelector(getPostsStatus);
    const error = useSelector(getPostsError);

    useEffect(() => {
        dispatch(fetchExplorePosts({userId: auth._id}))
    }, []);

    let content;
    if (postStatus === 'loading') {
        content = <Loading/>;
    } else if (postStatus === 'succeeded') {
        content = posts.map(post => <PostCard key={post.id} post={post} />)
    } else if (postStatus === 'failed') {
        content = <p>{error}</p>;
    }

    return (
        <div className="list-container">
            <div>
                <PostCard/>
                <PostCard/>
                <PostCard/>
                <PostCard/>
                <PostCard/>
            </div>
            <div className="home-side">
                <div className="home-side-user">
                    <img src={auth?.avatar? auth?.avatar : "/img/avatar.jpg"} alt=""/>
                    <h4>{auth?.username}</h4>
                </div>
                <div>
                    SUGGESTION
                </div>
                <Footer/>
            </div>
        </div>
    )
}

export default PostList
