import {useAuth} from "../../hooks";
import {useState} from "react";
import UploadImg from "../../components/UploadImg";

const ChangeAvatar = () => {

    const auth = useAuth()

    const [picture, setPicture] = useState(null);
    const [imgData, setImgData] = useState( auth?.avatar);

    return (
        <div className="change-avatar-container">
            <label htmlFor="file-upload" className="custom-avatar-upload">
                <img src={imgData? imgData : "/img/avatar.jpg"} alt=""/>
            </label>
            <UploadImg setImgData={setImgData} setPicture={setPicture}/>
            <div style={{width: "90px", margin:"40px auto"}}>
                <button className="btn-form">Upload</button>
            </div>
        </div>
    )
}

export default ChangeAvatar
