import './messages.css'
import {useDispatch, useSelector} from "react-redux";
import {getChatList, getChats, getChatStatus} from "../../redux/chatSlice";
import {useEffect} from "react";
import Loading from "../../components/Loading/Loading";
import {PostCard} from "../../components";

const Users = () => {

    const dispatch = useDispatch()
    const chats = useSelector(getChats)
    const status = useSelector(getChatStatus)

    useEffect(() => {
        dispatch(getChatList({id: auth._id}))
    }, []);

    let content;
    if (status === 'loading') {
        content = <Loading/>;
    } else if (status === 'succeeded') {
        /*const orderedPosts = chats.slice().sort((a, b) => b.createdAt.localeCompare(a.createdAt))*/
        content = chats.map((c,i) =>
            <div key={i} className="mess-user">
                <img src={c.avatar ? c.avatar : "/img/avatar.jpg"} alt=""/>
                <h4>{c.username}</h4>
            </div>
        )
    } else if (status === 'failed') {
        content = <p>ERROR</p>;
    }
    
    return (
        {content}
    )
}

export default Users
