import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import axios, {axiosPrivate} from "../axios/axios";

const initialState = {
    auth: null,
    accessToken: null,
    error : null
}

export const signIn = createAsyncThunk('auth/signIn',async ({email, pwd}, {rejectWithValue}) => {
    try {
        const response = await axios.post('/auth', {email, pwd}, {
            headers: { 'Content-Type': 'application/json' },
            withCredentials: true
        })
        return response.data
    } catch (err) {
        if (!err?.response) {
            return rejectWithValue('No Server Response');
        } else if (err.response?.status === 400) {
            return rejectWithValue('Missing Email or Password');
        } else if (err.response?.status === 401) {
            return rejectWithValue('Unauthorized')
        } else {
            return rejectWithValue('Login Failed')
        }
    }
})

export const signOut = createAsyncThunk('auth/signOut',async () => {
    try {
        const response = await axios.get('/logout', {
            withCredentials: true
        });
        return response.status
    } catch (err) {
        console.error(err);
    }
})

export const followUser = createAsyncThunk('auth/followUser', async ({id, userId}) => {
    try {
        const response = await axiosPrivate.patch(`/users/${id}/follow`, {userId})
        return response.data
    } catch (err) {
        return err.message
    }
})

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        refreshToken: (state, action) => {
            state.accessToken = action.payload
        },
    },
    extraReducers(builder){
        builder
            .addCase(signIn.fulfilled,(state,action) => {
                state.auth = action.payload.auth
                state.accessToken = action.payload.accessToken
                state.error = null
            })
            .addCase(signIn.rejected, (state, action) => {
                state.error = action.payload
            })
            .addCase(signOut.fulfilled,() => {
                return { ...initialState }
            })

            .addCase(followUser.fulfilled, (state, action) => {
                state.auth = action.payload.updatedUser
            })
    }
})

export const getAuth = (state) => state.auth.auth
export const getToken = (state) => state.auth.accessToken
export const getError = (state) => state.auth.error
export const {refreshToken} = authSlice.actions
export default authSlice.reducer
