import "./PostCard.css"
import {ChatBubbleOutline, Favorite,
    FavoriteBorder, Share,
    BookmarkBorder, MoreHoriz,
    SentimentSatisfiedAlt} from "@mui/icons-material";
import PostAuthor from "../../pages/PostDetail/postAuthor";
import TimeAgo from "../../pages/PostDetail/TimeAgo";
import PostAvatar from "../../pages/PostDetail/postAvatar";
import {useDispatch} from "react-redux";
import {useAuth} from "../../hooks";
import {likePost} from "../../redux/postsSlice";
import {useState} from "react";

const PostCard = ({post}) => {

    const auth = useAuth()
    const dispatch = useDispatch()

    const [likes, setLikes] = useState(post?.likes)
    const hasLikedPost = post.likes.find((like) => like === auth._id)

    const handleLike = () => {
        dispatch(likePost({id: post._id, userId: auth._id}))
        if (hasLikedPost) {
            setLikes(post.likes.filter((id) => id !== auth._id));
        } else {
            setLikes([...post.likes, auth._id]);
        }
    }

    const Likes = () => {
        if (likes.length > 0) {
            return likes.find((like) => like === auth._id)
                ? (
                    <><Favorite fontSize="large" />&nbsp;{likes.length > 2 ? `You and ${likes.length - 1} others` : `${likes.length} like${likes.length > 1 ? 's' : ''}` }</>
                ) : (
                    <><FavoriteBorder fontSize="large" />&nbsp;{likes.length} {likes.length === 1 ? 'Like' : 'Likes'}</>
                );
        }
        return <><FavoriteBorder fontSize="large" />&nbsp;Like</>;
    };

    return (
        <div className="post">
            <div className="info">
                <div className="user">
                    <div className="profile-pic">
                        <PostAvatar userID={post.userId}/>
                    </div>
                    <p className="username css-link">
                        <PostAuthor userID={post.userId}/>
                    </p>
                    <TimeAgo timestamp={post.createdAt}/>
                </div>
                <button><MoreHoriz/></button>
            </div>
            <div className="post-image">
                <img src={post.img} alt="" />
            </div>
            <div className="post-content">
                <div className="reaction-wrapper">
                    <div>
                        <button onClick={handleLike}>
                            <Likes/>
                        </button>
                        <button style={{margin: "0 15px"}}><ChatBubbleOutline fontSize="large"/></button>
                        <button><Share fontSize="large"/></button>
                    </div>
                    <button><BookmarkBorder fontSize="large"/></button>
                </div>
                <p className="likes">{likes.length} likes</p>
                <p className="description css-link">
                    <span><PostAuthor userID={post.userId}/> </span>
                    {post.desc}
                </p>
                <p className="post-cmt">View all {post.comments.length} comments</p>
            </div>
            <div className="comment-wrapper">
                <SentimentSatisfiedAlt/>
                <input type="text" className="comment-box" placeholder="Add a comment"/>
                <button className="comment-btn">post</button>
            </div>
        </div>
    )
}

export default PostCard
