import "./explore.css"
import PostSquare from "../../components/PostSquare/PostSquare";
import {useDispatch, useSelector} from "react-redux";
import {useEffect} from "react";
import {fetchProfilePosts, getPosts, getPostsError, getPostsStatus} from "../../redux/postsSlice";

const Explore = ({userId}) => {

    const dispatch = useDispatch()
    const posts = useSelector(getPosts)
    const postStatus = useSelector(getPostsStatus);
    const error = useSelector(getPostsError);

    useEffect(() => {
        dispatch(fetchProfilePosts({userId}))
    }, [userId]);

    let content;
    if (postStatus === 'loading') {
        content = <p>"Loading..."</p>;
    } else if (postStatus === 'succeeded') {
        content = posts.map(post => <PostSquare key={post.id} post={post} />)
    } else if (postStatus === 'failed') {
        content = <p>{error}</p>;
    }

    return (
        <div className="explore-container">
            <div className="gallery">
                {content}
            </div>
        </div>
    )
}

export default Explore
