import "./create.css"
import CloseIcon from '@mui/icons-material/Close';
import {useContext, useEffect, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {addNewPost, getPostsbyID, updatePost} from "../../redux/postsSlice";
import {useAuth} from "../../hooks";
import CreateContext from "../context/CreateProvider";
import UploadImg from "../UploadImg";

const Create = ({prevMenu, selectedMenu, setSelectedMenu, avatar, username}) => {

    const {toggleCreate, setToggleCreate, id, setId} = useContext(CreateContext)

    const post = useSelector((state) => getPostsbyID(state, id))

    const [caption, setCaption] = useState('')
    const [picture, setPicture] = useState(null);
    const [imgData, setImgData] = useState( null);

    const auth = useAuth()
    const dispatch = useDispatch()

    useEffect(() => {
        setCaption(post?.desc)
        setImgData(post?.img)
    }, [id]);

    const submitPost = (e) => {
        e.preventDefault()
        dispatch(!post ? addNewPost({
            id: auth._id,
            caption: caption,
            imgData
        }) : updatePost({
            id: id,
            caption: caption,
            imgData
        }))
            .unwrap()
            .then( () => {
                setCaption('')
                setPicture(null)
                setImgData(null)
            })
            .catch( err => {
                return err.messages
            })
    }

    return (
        <form>
            {toggleCreate && (
                <div className="overlay">
                    <button
                        className="closebtn"
                        onClick={() => {
                            setToggleCreate(false)
                            if (selectedMenu === "create") {setSelectedMenu(prevMenu)}
                            setCaption('')
                            setPicture(null)
                            setImgData(null)
                            setId('')
                        }}
                    >
                        <CloseIcon fontSize="large" sx={{ color: 'white' }}/>
                    </button>

                    <div className="overlay-content">
                        <div className="overlay-content-header">
                            {post ? "Edit your post" : "Create new post"}
                            <button
                                className="share-btn"
                                type="submit"
                                disabled={!caption || !imgData}
                                onClick={submitPost}
                            >
                                {post ? "Update" : "Share"}
                            </button>
                        </div>
                            <div className="overlay-content-main">

                                <div className="overlay-content-left">
                                    <div className="overlay-user">
                                        <div className="overlay-profile-pic">
                                            <img src={avatar? avatar : "/img/avatar.jpg"} alt=""/>
                                        </div>
                                        <p className="overlay-username">{username}</p>
                                    </div>
                                    <textarea
                                        placeholder="Write a caption..."
                                        value={caption}
                                        onChange={(e) => setCaption(e.target.value)}
                                    />
                                </div>

                                <div className="overlay-content-right">
                                    <label htmlFor="file-upload" className="custom-file-upload">
                                        Select image from computer
                                    </label>
                                    <UploadImg setImgData={setImgData} setPicture={setPicture}/>
                                    <img src={imgData? imgData : "/img/no-photo-available.png"} alt=""/>
                                </div>

                            </div>
                    </div>
                </div>
            )}
        </form>
    )
}

export default Create
