import "./postDetail.css"
import {Link, useParams} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {fetchCmt, fetchPostDetail, getCmt, getPost, getPostsStatus, likePost, reset} from "../../redux/postsSlice";
import {useEffect, useState} from "react";
import {useAuth} from "../../hooks";
import TimeAgo from "./TimeAgo";
import Loading from "../../components/Loading/Loading";

import {
    ChatBubbleOutline,
    FavoriteBorder, Share,
    BookmarkBorder, MoreHoriz,
    SentimentSatisfiedAlt, Favorite, Bookmark
} from "@mui/icons-material";
import {savePost} from "../../redux/usersSlice";
import Cmt from "./Cmt";

const PostDetail = () => {

    const auth = useAuth()
    const dispatch = useDispatch()
    const { postId } = useParams()
    const post = useSelector(getPost)
    const cmt = useSelector(getCmt)
    const status = useSelector(getPostsStatus)

    const [likes, setLikes] = useState([])
    const hasLikedPost = post?.likes?.find((like) => like === auth._id)
    const handleLike = () => {
        dispatch(likePost({id: post._id, userId: auth._id}))
            .unwrap()
            .then()
            .catch(err => {
                return err.message
            })
        if (hasLikedPost) {
            setLikes(post.likes.filter((id) => id !== auth._id));
        } else {
            setLikes([...post.likes, auth._id]);
        }
    }

    const hasSaved = auth.saved.find((save) => save === post?._id)
    const handleSave = () => {
        dispatch(savePost({id: auth._id, postId: post._id}))
            .unwrap()
            .then()
            .catch(err => {
                return err.message
            })
    }

    useEffect(() => {
        dispatch(fetchPostDetail({id: postId}))
        dispatch(fetchCmt({id:postId}))
        return () => {
            dispatch(reset())
        }
    }, [postId]);

    useEffect(() => {
        setLikes(post?.likes)
    },[post])

    if (status === "loading") {
        return <Loading/>
    } else if (status === "succeeded" && post === "Request failed with status code 500") {
        return (
            <section style={{margin: "auto"}}>
                <h2>Post not found!</h2>
            </section>
        )
    }

    return (
        <article className="detail-container">
            <div className="detail-img">
                <img src={post?.img} alt=""/>
            </div>

            <div className="detail-info">

                <div className="detail-info-header">
                    <div className="detail-user">
                        <Link to={`/${post?.username}`}>
                            <img src={post?.avatar ? post.avatar : "/img/avatar.jpg"} alt=""/>
                        </Link>
                        <Link to={`/${post?.username}`}>
                            {post ? post.username : 'Unknown author'}
                        </Link>
                        <p>&nbsp;â€¢ Follow</p>
                    </div>
                    <button><MoreHoriz/></button>
                </div>


                <div className="cmt-wrapper">

                    <div className="detail-info-cmt">
                        <div>
                            <Link to={`/${post?.username}`}>
                                <img src={post?.avatar ? post.avatar : "/img/avatar.jpg"} alt=""/>
                            </Link>
                        </div>
                        <div>
                            <div className="cmt-container">
                            <span className="cmt">
                                <Link to={`/${post?.username}`}>
                                    <span className="cmt-user">
                                    {post ? post.username : 'Unknown author'}
                                </span>
                                </Link>
                                {post?.desc}
                            </span>
                            </div>
                            <div className="cmt-time"><p>69h</p></div>
                        </div>
                    </div>

                    {cmt.map(cmt => <Cmt cmt={cmt}/>)}

                </div>

                <div className="detail-info-bot">
                    <div className="cmtbtn-container">
                        <div>
                            <button onClick={handleLike}>
                                {likes?.find((like) => like === auth._id)
                                    ? <Favorite fontSize="large" sx={{ color: "#f73378" }}/>
                                    : <FavoriteBorder fontSize="large"/>
                                }
                            </button>
                            <button style={{margin: "0 15px"}}><ChatBubbleOutline fontSize="large"/></button>
                            <button><Share fontSize="large"/></button>
                        </div>
                        <button onClick={handleSave}>
                            {hasSaved
                                ? <Bookmark fontSize="large"/>
                                : <BookmarkBorder fontSize="large"/>}
                        </button>
                    </div>
                    <p className="detail-likes">
                        {likes?.length} {likes?.length > 2 ? "likes" : "like"}
                    </p>
                    <p className="detail-time">
                        <TimeAgo timestamp={post?.createdAt}/>
                    </p>
                    <div className="detail-addcmt">
                        <SentimentSatisfiedAlt fontSize="large"/>
                        <input placeholder="Add a comment..."/>
                        <button>Post</button>
                    </div>
                </div>

            </div>
        </article>

    )
}

export default PostDetail
