import "./profile.css"
import PostSquare from "../../components/PostSquare/PostSquare";
import {Explore} from "../index";
import {Footer} from "../../components";
import {useAuth} from "../../hooks";
import {useParams} from "react-router-dom";
import {useEffect} from "react";
import {useDispatch, useSelector} from "react-redux";
import {getProfile, getUser} from "../../redux/usersSlice";

const Profile = () => {

    const auth = useAuth()
    const {username} = useParams()
    const dispatch = useDispatch()
    let user = useSelector(getProfile)


    useEffect(() => {
        if (auth.username !== username) {
            dispatch(getUser({username}))
            profile = user
        } 
    }, [profile]);

    let profile
    if (auth.username === username) {
        profile = auth
    } else {
        profile = user
    }


    return (
        <main className="profile-container">
            <div className="main-profile">
                <div className="main-profile-left">
                    <img src={auth?.avatar? auth?.avatar : "/img/no-photo-available.png"} alt=""/>
                </div>
                <div className="main-profile-right">
                    <div className="right-top">
                        <span>{profile?.username}</span>
                        <button>Follow</button>
                        <button>Message</button>
                    </div>
                    <div className="right-mid">
                        <ul>
                            <li><span className="profile-stat-count">164</span> posts</li>
                            <li><span className="profile-stat-count">{auth?.followers.length}</span> followers</li>
                            <li><span className="profile-stat-count">{auth?.followings.length}</span> following</li>
                        </ul>
                    </div>
                    <div className="right-bot">
                        <p className="profile-name">Real Madrid C.F.</p>
                        <p>
                            ‚öΩÔ∏è Official profile of Real Madrid C.F.<br/>
                            üèÜ 14 times European Champions<br/>
                            üåç FIFA Best Club of the 20th Century<br/>
                            üì± #RealFootball | ü§ç #MADRIDISTAS
                        </p>
                    </div>
                </div>
            </div>
            <Explore/>
            <Footer/>
        </main>
    )
}

export default Profile
