import "./search.css"
import UsersList from "../../components/UsersList/UsersList";
import {useEffect, useRef, useState} from "react";
import {Link} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {addHistory, fetchSearch, getSearch, getSearchStatus} from "../../redux/searchSlice";
import useDebounce from "../../hooks/useDebounce";
import Loading from "../../components/Loading/Loading";
import {PostCard} from "../../components";
import {useAuth} from "../../hooks";

const Search = ({collapse, setCollapse, setSelectedMenu, location}) => {

    const auth = useAuth()
    const dispatch = useDispatch()
    const searchResult = useSelector(getSearch)
    const searchStatus = useSelector(getSearchStatus)
    const refSearch = useRef()

    const [search, setSearch] =useState('')
    const debouncedSearchTerm = useDebounce(search, 1000);

    useEffect(() => {
        if (debouncedSearchTerm) {
            const promise = dispatch(fetchSearch({
                search: encodeURIComponent(search)
            }))
            return () => {
                // `createAsyncThunk` attaches an `abort()` method to the promise
                promise.abort()
            }
        }
    },[debouncedSearchTerm])

    const close = () => {
        setCollapse(false);
        setSelectedMenu(location.pathname)
        setSearch('')
    }

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (refSearch.current && !refSearch.current.contains(event.target)) {
                close()
            }
        };
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, [refSearch]);

    let content;
    if (searchStatus === 'loading') {
        content = <Loading/>;
    } else if (searchStatus === 'succeeded') {
        content = searchResult.map((s,index) =>
            <Link to={`/${s._id ? s.username : `explore/${s.tag}`}`} className="user_list search-hover" key={index}>
                <div
                    className="user_list_detail"
                    onClick={() => {
                        close()
                        dispatch(s._id ? addHistory({
                            id: auth._id,
                            history: {userID: s._id}
                        }) : addHistory({
                            id: auth._id,
                            history: {tag: s.tag}
                        }))
                    } }
                >
                    <img
                        src={s._id
                            ? s.avatar ? s.avatar : "/img/avatar.jpg"
                            : "/img/Number_sign.png"}
                        alt=""
                    />
                    &nbsp;&nbsp;{s._id ? s.username : s.tag}<br/>
                    {!s._id && <p>{s.count} post</p>}
                </div>
            </Link>
        )
    } else if (searchStatus === 'failed') {
        content = <p>ERROR</p>;
    }

    return (
        <section>
            {collapse && (
                <div className="search-bar" ref={refSearch}>
                    <div>
                        <h1>Search</h1>
                        <input
                            value={search}
                            placeholder="Search"
                            onChange={(e) => setSearch(e.target.value)}
                        />
                    </div>
                    <div className="search-preview">
                        {content}
                    </div>
                </div>
            )}
        </section>
    )
}

export default Search
