import './messages.css'
import {useAuth} from "../../hooks";
import Users from "./Users";
import {SentimentSatisfiedAlt} from "@mui/icons-material";
import {useEffect, useRef, useState} from "react";
import {useDispatch, useSelector} from "react-redux";
import {clearChat, getChatList, getChats, getMess, newMessage} from "../../redux/chatSlice";
import {Link} from "react-router-dom";
import Mess from "./Mess";
import { io } from "socket.io-client";

const Messages = () => {

    const auth = useAuth()
    const mess = useSelector(getMess)
    const dispatch = useDispatch()

    const [newMess, setNewMess] = useState('')
    const [header, setHeader] = useState({
        img: '',
        username: ''
    })

    const socket = useRef()
    useEffect(() => {
        socket.current = io("ws://localhost:8900")
    },[])
    useEffect(() => {
        socket?.on("welcome", message => {
            console.log(message)
        })
    },[socket])

    useEffect(() => {
        return () => {
            dispatch(clearChat())
        }
    },[])

    return (
        <div className="mess-page">
            <div className="mess-container">
                <div className="mess-users">
                    <div className="mess-auth">
                        <h4>{auth.username}</h4>
                    </div>
                    <div style={{padding: '10px'}}>
                        <Users setHeader={setHeader}/>
                    </div>
                </div>

                <div className="mess-main">
                    {mess !== null && (
                        <>
                            <Link to={`/${header.username}`} className="mess-user mess-main-header">
                                <img src={header.img} alt=""/>
                                <h4>{header.username}</h4>
                            </Link>
                            <div className="mess-inbox">
                                {mess.messages.map((m,i) =>
                                    <Mess m={m} key={i} own={m.userId === auth._id} user={header}/>
                                )}
                            </div>

                            <div className="mess-input">
                                <button><SentimentSatisfiedAlt fontSize="large"/></button>
                                <textarea
                                    placeholder="Aa"
                                    value={newMess}
                                    onChange={(e) => setNewMess(e.target.value)}
                                />
                                <button
                                    onClick={() =>
                                        dispatch(newMessage({id: mess._id, userId: auth._id, mess: newMess}))
                                }
                                >Send</button>
                            </div>
                        </>
                    )}
                </div>
            </div>
        </div>
    )
}

export default Messages
