import './usersList.css'
import {Link} from "react-router-dom";
import {useAuth} from "../../hooks";
import {useDispatch} from "react-redux";
import {followUser} from "../../redux/usersSlice";

const UsersList = ({user}) => {

    const auth = useAuth()
    const dispatch = useDispatch()

    return (
        <div className="user_list">
            <div className="user_list_detail">
                <Link to={`/${user.username}`}>
                    <img src={user.avatar ? user.avatar : "/img/avatar.jpg"} alt=""/>
                </Link>
                <Link to={`/${user.username}`}>
                    &nbsp;&nbsp;{user.username}
                </Link>
            </div>
            <button
                onClick={() => dispatch(followUser({
                    id: auth._id,
                    userId: user._id
                }))}
            >
                {auth.followings.find((follow) => follow === user._id) ? 'Following' : 'Follow'}
            </button>
        </div>
    )
}

export default UsersList
