import {createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import {axiosPrivate} from "../axios/axios";

const initialState = {
    user: null,
    profile: null,
}

export const getAllUsers = createAsyncThunk('user/getAllUsers', async () => {
    try {
        const response = await axiosPrivate.get(`/users`)
        return response.data
    } catch (err) {
        return err.message
    }
})

export const fetchUser = createAsyncThunk('user/fetchUser',async ({username}) => {
    try {
        const response = await axiosPrivate.get(`/users/${username}`)
        return response.data
    } catch (err) {
        return err.message
    }
})

export const fetchNameAndAvatar = createAsyncThunk('user/fetchNameAndAvatar', async ({userId}) => {
    try {
        const response = await axiosPrivate.get(`/users/p/${userId}`)
        return response.data
    } catch (err) {
        return err.message
    }
})

const usersSlice = createSlice({
    name: 'users',
    initialState,
    reducers: {
        clearUsers(state,action) {
            return {...initialState}
        }
    },
    extraReducers(builder){
        builder

            .addCase(getAllUsers.fulfilled,(state,action) => {

            })

            .addCase(fetchUser.fulfilled,(state,action) => {
                state.profile = action.payload
            })
            .addCase(fetchUser.rejected,(state, action) => {
                console.log(action.err.message)
            })

            .addCase(fetchNameAndAvatar.fulfilled, (state, action) => {
                state.user = action.payload
            })
    }
})

export const getUser = (state) => state.users.user
export const getProfile = (state) => state.users.profile
export const {clearUsers} = usersSlice.actions
export default usersSlice.reducer
