import './management.css'
import {useDispatch} from "react-redux";
import {clearStatistics, getAllUsers} from "../../redux/adminSlice";
import {useEffect} from "react";
import {Link} from "react-router-dom";

const Management = () => {

    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(getAllUsers())
        return () => {
            dispatch(clearStatistics())
        };
    }, []);

    return (
        <div className="management-container">
            <h1>User Management</h1>
            <input
                placeholder="Search..."
            />
            <div className="management-header">
                <h4 className="management-header-item">id</h4>
                <h4 className="management-header-item">Username</h4>
                <h4 className="management-header-item">Email</h4>
            </div>
            <div>
                <div className="user_list">
                    <div className="user_list_detail">
                        <p style={{width: '200px'}}>fasfbsift783f</p>
                        &nbsp;&nbsp;
                        <Link to={'/'} style={{width: '200px', display: 'flex', alignItems: 'center'}}>

                                <img src="/img/avatar.jpg" alt=""/>


                                &nbsp;&nbsp;User

                        </Link>
                        &nbsp;&nbsp;
                        <p style={{width: '150px'}}>69@69</p>
                    </div>
                    <div>
                        <button className='following'>Edit</button>
                        &nbsp;&nbsp;
                        <button className='following'>Ban</button>
                        &nbsp;&nbsp;
                        <button className='following'>Delete</button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Management
