import {useState, useEffect} from "react";
import {Link} from "react-router-dom";
import "./register.css";
import {Google_btn, Footer, OR} from "../../components";
import CheckIcon from '@mui/icons-material/Check';
import ClearIcon from '@mui/icons-material/Clear';

const USER_REGEX = /^[A-z][A-z0-9-_]{3,23}$/;
const PWD_REGEX = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9]).{8,24}$/;

const Register = () => {

    const [user, setUser] = useState('');
    const [validName, setValidName] = useState(false);
    const [userFocus, setUserFocus] = useState(false);

    const [pwd, setPwd] = useState('');
    const [validPwd, setValidPwd] = useState(false);
    const [pwdFocus, setPwdFocus] = useState(false);

    const [matchPwd, setMatchPwd] = useState('');
    const [validMatch, setValidMatch] = useState(false);
    const [matchFocus, setMatchFocus] = useState(false);

    const [errMsg, setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(() => {
        setValidName(USER_REGEX.test(user));
    }, [user])

    useEffect(() => {
        setValidPwd(PWD_REGEX.test(pwd));
        setValidMatch(pwd === matchPwd);
    }, [pwd, matchPwd])

    useEffect(() => {
        setErrMsg('');
    }, [user, pwd, matchPwd])

    const handleSubmit = () => {

    }

    return (
        <div className="register">
            <div className="box">
                <div className="goto">
                    <h1>Coonstagram</h1>
                    <h5>Sign up to see photos and videos from your friends.</h5>
                    <Google_btn/>
                </div>
                <OR/>
                <form className="login-form" onSubmit={handleSubmit}>
                    <div className="field">
                        <input
                            id="email"
                            type="email"
                            placeholder="Email"
                        />
                        <label htmlFor="email">Email</label>
                        <CheckIcon className="check" fontSize="small" color="success"/>
                    </div>
                    <div className="field">
                        <input
                            id="username"
                            type="text"
                            placeholder="Username"
                        />
                        <label htmlFor="username">Username</label>
                    </div>
                    <div className="field">
                        <input
                            id="password"
                            type="password"
                            placeholder="Password"
                        />
                        <label htmlFor="password">Password</label>
                    </div>
                    <div className="field">
                        <input
                            id="conf_pw"
                            type="password"
                            placeholder="Confirm Password"
                        />
                        <label htmlFor="conf_pw">Confirm Password</label>
                    </div>
                    <p className="etc">
                        By signing up, you agree to our Terms , Privacy Policy and Cookies Policy .
                    </p>
                    <button
                        className="btn-register btn-form"
                        disabled={!validName || !validPwd || !validMatch}
                    >Sign Up</button>
                </form>
            </div>
            <div className="box goto">
                <p>
                    Have an account ?
                    <Link to="/login"> Log In</Link>
                </p>
            </div>
            <Footer/>
        </div>
    )
}

export default Register
