import "./sideMenu.css"
import {Link, useLocation} from "react-router-dom";
import {useState, useEffect} from "react";
import {usePrevious} from "../../hooks/usePrevious";
import More from "./More";

import HomeOutlinedIcon from '@mui/icons-material/HomeOutlined';
import HomeIcon from '@mui/icons-material/Home';
import SearchIcon from '@mui/icons-material/Search';
import ExploreOutlinedIcon from '@mui/icons-material/ExploreOutlined';
import ExploreIcon from '@mui/icons-material/Explore';
import SendTimeExtensionOutlinedIcon from '@mui/icons-material/SendTimeExtensionOutlined';
import SendTimeExtensionIcon from '@mui/icons-material/SendTimeExtension';
import NotificationsActiveOutlinedIcon from '@mui/icons-material/NotificationsActiveOutlined';
import AddBoxOutlinedIcon from '@mui/icons-material/AddBoxOutlined';
import FaceOutlinedIcon from '@mui/icons-material/FaceOutlined';

const SideMenu = ({selectedMenu, setSelectedMenu, setToggleCreate}) => {

    return (
        <div className="side">
            <div className="side-logo">
                <Link to="/">
                    <h3>Coonstagram</h3>
                </Link>
            </div>

            <div className="space">
                <div className="menu-btn container-side">

                    <Link to="/">
                        <button className={selectedMenu === "/" ? "selected" : ""}>
                        <span>
                            {selectedMenu === "/" ? <HomeIcon fontSize="large"/> : <HomeOutlinedIcon fontSize="large"/>}
                        </span>
                            <span>&nbsp;&nbsp;&nbsp;Home</span>
                        </button>
                    </Link>

                    <button>
                        <span><SearchIcon fontSize="large"/></span>
                        <span>&nbsp;&nbsp;&nbsp;Search</span>
                    </button>

                    <Link to="/explore">
                        <button className={selectedMenu === "/explore" ? "selected" : ""}>
                        <span>
                            {selectedMenu === "/explore" ? <ExploreIcon fontSize="large"/> : <ExploreOutlinedIcon fontSize="large"/>}
                        </span>
                            <span>&nbsp;&nbsp;&nbsp;Explore</span>
                        </button>
                    </Link>

                    <Link to="/inbox">
                        <button className={selectedMenu === "/inbox" ? "selected" : ""}>
                        <span>
                            {selectedMenu === "/inbox" ? <SendTimeExtensionIcon fontSize="large"/> : <SendTimeExtensionOutlinedIcon fontSize="large"/>}
                        </span>
                            <span>&nbsp;&nbsp;&nbsp;Messages</span>
                        </button>
                    </Link>

                    <button>
                        <span><NotificationsActiveOutlinedIcon fontSize="large"/></span>
                        <span>&nbsp;&nbsp;&nbsp;Notifications</span>
                    </button>

                    <button
                        className={selectedMenu === "create" ? "selected" : ""}
                        onClick={() => {
                            setToggleCreate(true)
                            setSelectedMenu('create')
                        }}
                    >
                        <span><AddBoxOutlinedIcon fontSize="large"/></span>
                        <span>&nbsp;&nbsp;&nbsp;Create</span>
                    </button>

                    <Link to="/profile">
                        <button>
                            <span><FaceOutlinedIcon fontSize="large"/></span>
                            <span>&nbsp;&nbsp;&nbsp;Profile</span>
                        </button>
                    </Link>

                </div>
                <More/>
            </div>
        </div>
    )
}

export default SideMenu
