import {useEffect} from "react";
import {fetchCmt, fetchHomePosts, getCmt, getCmtStatus, getPostsError, getPostsStatus} from "../../redux/postsSlice";
import {getSuggestion} from "../../redux/usersSlice";
import Loading from "../../components/Loading/Loading";
import {PostCard} from "../../components";
import {useAuth} from "../../hooks";
import {useDispatch, useSelector} from "react-redux";
import TimeAgo from "./TimeAgo";

const Cmt = ({cmt}) => {



    return (
        <div className="detail-info-cmt">
            <div><img src={cmt?.avatar ? cmt.avatar : "/img/avatar.jpg"} alt=""/></div>
            <div>
                <div className="cmt-container">
                            <span className="cmt">
                                <span className="cmt-user">{cmt.username}</span>
                                {cmt.cmt}
                            </span>
                </div>
                <div className="cmt-time"><p><TimeAgo timestamp={cmt.date}/></p></div>
            </div>
        </div>
    )
}

export default Cmt
